import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [hospitalID, setHospitalID] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Hardcoded credentials for simplicity (replace with backend authentication later)
    const validHospitalID = "hospital123";
    const validPassword = "password123";

    if (hospitalID === validHospitalID && password === validPassword) {
      setError(""); // Clear errors
      navigate("/healthcare"); // Redirect to Healthcare page
    } else {
      setError("Invalid Hospital ID or Password.");
    }
  };

  return (
    <div className="login-container">
      <h1>Hospital Login</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Hospital ID"
          value={hospitalID}
          onChange={(e) => setHospitalID(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default Login;
