import React, { useState } from "react";
import { ethers } from "ethers";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const PatientLogin = () => {
  const [patientID, setPatientID] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [walletAddress, setWalletAddress] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!patientID || !password) {
      setError("Please enter both Patient ID and Password.");
      return;
    }

    if (typeof window.ethereum === "undefined") {
      setError("MetaMask is not installed. Please install it to proceed.");
      return;
    }

    try {
      // Prompt MetaMask to connect
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const address = await signer.getAddress();

      // Display connected wallet address
      setWalletAddress(address);

      // Example: Generate a signature for login
      const message = `Login request for Patient ID: ${patientID}`;
      const signature = await signer.signMessage(message);

      console.log("Signature:", signature);

      // Verify login credentials (can be extended with backend validation)
      const validPatientID = "patient123";
      const validPassword = "mypassword123";

      if (patientID === validPatientID && password === validPassword) {
        // Perform wallet verification (example logic)
        const validWalletAddress = "0x7b24aD4F4F2C374D3f21Ce3211aE86e1C01F99Ce";
        if (address.toLowerCase() === validWalletAddress.toLowerCase()) {
          setError("");
          navigate("/patient-dashboard"); // Redirect on success
        } else {
          setError("Unauthorized wallet address.");
        }
      } else {
        setError("Invalid Patient ID or Password.");
      }
    } catch (err) {
      console.error("MetaMask Login Error:", err);
      setError("Failed to authenticate with MetaMask.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Patient Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Patient ID"
            value={patientID}
            onChange={(e) => setPatientID(e.target.value)}
            className="login-input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="login-input"
            required
          />
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
        {walletAddress && <p>Connected Wallet: {walletAddress}</p>}
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
};

export default PatientLogin;
