import React, { useState } from "react";
import "./Login.css";

const Login = () => {
  const [activeLogin, setActiveLogin] = useState("Hospital");

  const handleLoginChange = (type) => {
    setActiveLogin(type);
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>{activeLogin} Login</h1>

        {/* Login type selector */}
        <div className="login-selector">
          <button
            className={`login-type-button ${activeLogin === "Hospital" ? "active" : ""}`}
            onClick={() => handleLoginChange("Hospital")}
          >
            Hospital
          </button>
          <button
            className={`login-type-button ${activeLogin === "Doctor" ? "active" : ""}`}
            onClick={() => handleLoginChange("Doctor")}
          >
            Doctor
          </button>
          <button
            className={`login-type-button ${activeLogin === "Patient" ? "active" : ""}`}
            onClick={() => handleLoginChange("Patient")}
          >
            Patient
          </button>
        </div>

        {/* Login form */}
        <form>
          <input
            type="text"
            placeholder="Email"
            className="login-input"
          />
          <input
            type="password"
            placeholder="Password"
            className="login-input"
          />
          <button type="submit" className="login-button">
            Login as {activeLogin}
          </button>
        </form>

        {/* Footer */}
        <div className="login-footer">
          <p>
            Need help? <a href="#">Contact Support</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
